SELECT * FROM HARVEST

SELECT
  f.farmer_id,
  f.name,
  f.region,
  h.harvest_year,
  p.name AS product,
  SUM(h.harvested_kg) AS gross_kg,
  CAST(SUM(h.harvested_kg) * 0.02 AS DECIMAL(12,3)) AS tare_kg,
  CAST(SUM(h.harvested_kg) * 0.98 AS DECIMAL(12,3)) AS net_kg
FROM HARVEST h
JOIN FARMER f  ON f.farmer_id = h.farmer_id
JOIN PRODUCT p ON p.product_id = h.product_id
WHERE h.harvest_year = 2025
GROUP BY f.farmer_id, f.name, f.region, h.harvest_year, p.name
ORDER BY f.region, p.name, f.farmer_id;

